RULES_ITEM.SQL_TEXT:
declare
    c_time_interval constant float := 5/24/60; --5 minute interval
    c_invalid_attempts constant number := 4;
    v_uid number;
    v_un varchar2(100);
begin
    v_uid := :USER_ID;
    for rec in (select client_ip, count(client_ip) attempts from usage_log ul, users u
                    where action_id = 9 and ul.un = u.un and u.user_id = v_uid
                    and iis_start between sysdate - c_time_interval and sysdate group by client_ip)
    loop
        if (rec.attempts > c_invalid_attempts) then
            update users set lock_expires = sysdate + c_time_interval where user_id = v_uid;
            exit;
        end if;
    end loop;
end;

declare
    c_complexity_rules constant number := 4;
    c_required_complexity_rules constant number := 3;
    c_min_pwd_length constant number := 10;
    c_max_pwd_length constant number := 128;
    v_password varchar2(255);
    v_msg varchar2(2048);
    v_first_rule_msg varchar2(300);
    v_second_rule_msg varchar2(1748);
    v_complexity_counter number;
begin
    v_password := pkg_vqsecurity.newpassword;
    -- 1. Password Length
    if (length(v_password) < c_min_pwd_length) then
        v_first_rule_msg := 'Minimum length of the password should be more than 10 characters.';
    elsif (length(v_password) > c_max_pwd_length) then
        v_first_rule_msg := 'Maximum length of the password should be NO more than 128 characters.';
    end if;

    -- 2. Password Complexity
    -- Accourding to the OWASP, a password must meet at least 3 out of the following 4 complexity rules:
    -- https://www.owasp.org/index.php/Authentication_Cheat_Sheet#Implement_Proper_Password_Strength_Controls
    v_complexity_counter := c_complexity_rules;
    if (not regexp_like(v_password, '[A-Z]')) then
        v_second_rule_msg := v_second_rule_msg || 'at least 1 uppercase character (A-Z)' || chr(10);
        v_complexity_counter := v_complexity_counter - 1;
    end if;

    if (not regexp_like(v_password, '[a-z]')) then
        v_second_rule_msg := v_second_rule_msg || 'at least 1 lowercase character (a-z)' || chr(10);
        v_complexity_counter := v_complexity_counter - 1;
    end if;

    if (not regexp_like(v_password, '[0-9]')) then
        v_second_rule_msg := v_second_rule_msg || 'at least 1 digit (0-9)' || chr(10);
        v_complexity_counter := v_complexity_counter - 1;
    end if;

    if (not regexp_like(v_password, '\W')) then
        v_second_rule_msg := v_second_rule_msg || 'at least 1 special character (punctuation)' || chr(10);
        v_complexity_counter := v_complexity_counter - 1;
    end if;

    -- The additional rule for checking if a password contains more than 2 indentical charaters in a row
    if (regexp_like(v_password, '([a-z\d])\1{2,}')) then
        v_second_rule_msg := v_second_rule_msg || 'not more than 2 identical characters in a row' || chr(10);
    end if;

    if (v_first_rule_msg is not null) then
        v_msg := 'Your password must meet the following rule about the password''s length:'
            || chr(10) || v_first_rule_msg;
    end if;

    if ((v_complexity_counter < c_required_complexity_rules)
            and (v_second_rule_msg is not null)) then
        v_msg := v_msg || chr(10) || chr(10) || 'Your password must meet the following complexity rules:'
            || chr(10) || v_second_rule_msg;
    end if;

    if (v_msg is not null) then
        util.RaiseError(v_msg);
    end if;
end;

declare
    v_comment varchar2(4000);
begin
    v_comment := util.getvalstrbyid(:pk, id.action_items.cf.act_action_comments);
    if v_comment is null then return; end if;
    util.setvalcharbyid(:pk, id.action_items.cf.act_action_comment_history, util.getcurrentuser() || ' ' || to_char(current_date, 'mm/dd/yyyy hh12:miAM') || chr(13) || v_comment || chr(13) || util.getvalstrbyid(:pk, id.action_items.cf.act_action_comment_history));
    util.setvalcharbyid(:pk, id.action_items.cf.act_action_comments, null);
end;

declare
    v_date date;
begin
    select max(ts)
      into v_date
      from audit_log_cflite
     where column_name = to_char(id.action_items.cf.act_action_comment_history)
       and pk = :pk;   
    util.setvaldatebyid(:pk, id.action_items.cf.act_time_last_update, v_date);
exception when no_data_found then
    util.setvaldatebyid(:pk, id.action_items.cf.act_time_last_update, null);
end;

declare
    v_xitor_id number;
    v_text varchar2(4000);
begin

    select chat.trackor_id, cm.text
      into v_xitor_id, v_text
      from chat
      join chat_message cm on (cm.chat_id = chat.chat_id)
     where cm.chat_message_id = :chat_message_id;

    if util.isflagset('ACT_ACTION_COMMENTS to chat ' || v_xitor_id) = 1 then
        util.clearflag('ACT_ACTION_COMMENTS to chat ' || v_xitor_id);
        return;
    end if;

    util.setflag('ACT_ACTION_COMMENTS to chat ' || v_xitor_id);

    util.setvalcharbyid(v_xitor_id, id.action_items.cf.act_action_comments, v_text);

end;

begin

    for rec in (
        select x.xitor_id
          from xitor x
          join config_value_char cvc on
              cvc.key_value = x.xitor_id
              and cvc.config_field_id = id.action_items.cf.act_age_of_last_update
              and cvc.value_char is not null
          join config_value_number cvn on
              cvn.key_value = x.xitor_id
              and cvn.config_field_id = id.action_items.cf.act_action_status
              and cvn.value_number <> id.vt.v_act_action_status.opened
         where x.xitor_type_id = id.action_items.tt
    ) loop
        util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, null);
    end loop;

    for rec in (
        select x.xitor_id,
               cvd.value_date
          from xitor x
          join config_value_date cvd on
              cvd.key_value = x.xitor_id
              and cvd.config_field_id = id.action_items.cf.act_time_last_update
              and cvd.value_date is not null
          join config_value_number cvn on
              cvn.key_value = x.xitor_id
              and cvn.config_field_id = id.action_items.cf.act_action_status
              and cvn.value_number = id.vt.v_act_action_status.opened
         where x.xitor_type_id = id.action_items.tt
    ) loop
        if round((current_date - rec.value_date) * 24 * 60) >= 1440 then
            util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date))) || ' Days');
        elsif round((current_date - rec.value_date) * 24 * 60) >= 60 then
            util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date) * 24)) || ' Hours');
        else
            util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date) * 24 * 60)) || ' Minutes');
        end if;
    end loop;

end;

declare
    v_date date;
begin

    v_date := util.getvaldatebyid(:pk, id.action_items.cf.act_due_date);
    if nvl(util.getvalnumbyid(:pk, id.action_items.cf.act_action_status), 0) <> 10023835 /*Open*/ then
        util.setvalnumbyid(:pk, id.action_items.cf.act_days_until_due, 0);
    elsif v_date is null then
        util.setvalnumbyid(:pk, id.action_items.cf.act_days_until_due, null);
    else
        util.setvalnumbyid(:pk, id.action_items.cf.act_days_until_due, round(v_date - current_date));
    end if;

end;

declare
    v_act_due_date date;
begin

    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.action_items.tt
    ) loop
        v_act_due_date := util.getvaldatebyid(rec.xitor_id, id.action_items.cf.act_due__forecast_date);
        if nvl(util.getvalnumbyid(rec.xitor_id, id.action_items.cf.act_action_status), -1) <> 10023835 /*Open*/ then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, null, 1);
            continue;
        end if;

        if v_act_due_date is null then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, null, 1);
        elsif v_act_due_date - current_date < 0 then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025266 /*Past Due*/, 1);
        elsif v_act_due_date - current_date <= 7 then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025267 /*Within 7-days*/, 1);
        elsif v_act_due_date - current_date <= 30 then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025268 /*Within 30-days*/, 1);
        elsif v_act_due_date - current_date <= 60 then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025269 /*Within 60-days*/, 1);
        elsif v_act_due_date - current_date <= 90 then
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025270 /*Within 90-days*/, 1);
        else
            util.setvalnumbyid(rec.xitor_id, 1000066982 /*id.action_items.cf.act_action_due*/, 10025271 /*>90 Days*/, 1);
        end if;
    end loop;

end;

declare
    v_current_time varchar2 (4000) := to_char(current_date, 'HH24:MI');
    v_current_date date := to_date(to_char(current_date, 'MM/DD/YYYY'), 'MM/DD/YYYY');
    v_project_id number;
    v_project_key varchar2 (4000);
    v_email varchar2 (4000);
begin

    for cur in (select x.xitor_id,
                       x.xitor_key,
                       u.email
                  from xitor x
                  join config_value_number cvn1 on (
                      cvn1.key_value = x.xitor_id
                                               and
                                                             cvn1.config_field_id = id.action_items.cf.act_enable_notifications
                                               and cvn1.value_number = 1)
                  join config_value_number cvn2 on (
                      cvn2.key_value = x.xitor_id
                                               and
                                                             cvn2.config_field_id = id.action_items.cf.act_action_status
                                               and cvn2.value_number = 10023835)
                  join config_value_number cvn3 on (
                      cvn3.key_value = x.xitor_id
                                               and
                                                             cvn3.config_field_id = id.action_items.cf.act_action_assigned_to
                                               and cvn3.value_number is not null)
                  join users u on(u.xitor_id = cvn3.value_number)

                 where x.xitor_type_id = id.action_items.tt)
    loop
        v_project_id := util.getparentbyid(cur.xitor_id, id.project.tt);

        if v_project_id is not null then
            v_project_key := util.getvalstrbyid(v_project_id, id.action_items.cf.xitor_key);
        end if;

        v_email := cur.email;

        for rec in (select u.email
                      from config_value_mult cvm
                      join users u on(u.xitor_id = cvm.value_number)
                     where cvm.key_value = cur.xitor_id
                       and cvm.config_field_id = id.action_items.cf.act_action_followers)
        loop
            v_email := v_email || ', ' || rec.email;
        end loop;

        util.sendmail('OneVizion - Action Item Notification', 'This Action Item record has reached the scheduled notification date. Please complete the open action item. <br>OneVizion Link: https://dev-apps.onevizion.com/form/ConfigAppForm.do?id=' || cur.xitor_id || '&ttid=100006856 <br>Action ID: ' || cur.xitor_key || ' <br>Parent Record(s): ' || v_project_key || ' <br>Action Name: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_action_item_name) || ' <br>Status: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_action_status) || ' <br>Category: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_action_category) || ' <br>Priority: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_priority) || ' <br>Department: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_department_owner) || ' <br>Due / Forecast Date: ' || util.getvaldatebyid(cur.xitor_id, id.action_items.cf.act_due__forecast_date) || ' <br>Future Notification Date: ' || util.getvaldatebyid(cur.xitor_id, id.action_items.cf.act_future_notification_date) || ' <br>Action Assigned To: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_action_assigned_to) || ' <br>Action Comments: ' || util.getvalstrbyid(cur.xitor_id, id.action_items.cf.act_action_comments) || ' <br>---', '', v_email, null, null, 'html');
    end loop;

end;

begin
for rec in ( select x.xitor_id from xitor x join config_value_char cvc on cvc.key_value = x.xitor_id and cvc.config_field_id = id.action_items.cf.act_age_of_action_item and cvc.value_char is not null join config_value_number cvn on cvn.key_value = x.xitor_id and cvn.config_field_id = id.action_items.cf.act_action_status and cvn.value_number <> id.vt.v_act_action_status.opened where x.xitor_type_id = id.action_items.tt and x.xitor_id = :pk ) loop util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, null); end loop;
for rec in ( select x.xitor_id, cvd.value_date from xitor x join config_value_date cvd on cvd.key_value = x.xitor_id and cvd.config_field_id = id.action_items.cf.act_action_created_date and cvd.value_date is not null join config_value_number cvn on cvn.key_value = x.xitor_id and cvn.config_field_id = id.action_items.cf.act_action_status and cvn.value_number = id.vt.v_act_action_status.opened where x.xitor_type_id = id.action_items.tt and x.xitor_id = :pk ) loop if round((current_date - rec.value_date) * 24 * 60) >= 1440 then util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date))) || ' Days'); elsif round((current_date - rec.value_date) * 24 * 60) >= 60 then util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date) * 24)) || ' Hours'); else util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date) * 24 * 60)) || ' Minutes'); end if; end loop;
end;

declare
    v_act_due_date date;
begin

    v_act_due_date := util.getvaldatebyid(:pk, id.action_items.cf.act_due__forecast_date);
    if nvl(util.getvalnumbyid(:pk, id.action_items.cf.act_action_status), -1) <> 10023835 /*Open*/ then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, null, 1);
        return;
    end if;

    if v_act_due_date is null then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, null, 1);
    elsif v_act_due_date - current_date < 0 then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025266 /*Past Due*/, 1);
    elsif v_act_due_date - current_date <= 7 then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025267 /*Within 7-days*/, 1);
    elsif v_act_due_date - current_date <= 30 then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025268 /*Within 30-days*/, 1);
    elsif v_act_due_date - current_date <= 60 then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025269 /*Within 60-days*/, 1);
    elsif v_act_due_date - current_date <= 90 then
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025270 /*Within 90-days*/, 1);
    else
        util.setvalnumbyid(:pk, 1000066982 /*id.action_items.cf.act_action_due*/, 10025271 /*>90 Days*/, 1);
    end if;

end;

