declare
    v_chat_msg_id number;
    v_chat_id number;
    v_temp varchar2(4000);
begin

    if util.getvalstrbyid(:pk, id.action_items.cf.act_action_comments) is null then
        return;
    end if;

    if util.isflagset('ACT_ACTION_COMMENTS to chat ' || :pk) = 1 then
        util.clearflag('ACT_ACTION_COMMENTS to chat ' || :pk);
        return;
    end if;

    util.setflag('ACT_ACTION_COMMENTS to chat ' || :pk);

    begin
        select chat_id
          into v_chat_id
          from chat
         where trackor_id = :pk;
    exception when no_data_found then
        v_chat_id := null;
    end;

    if v_chat_id is null then
        v_chat_id := pkg_chat.create_trackor_chat(:pk);
    end if;

    v_chat_msg_id := pkg_chat.create_msg_temp(v_chat_id, pkg_sec.get_cu(), util.getvalstrbyid(:pk, id.action_items.cf.act_action_comments));
    pkg_chat.finalize_msg_and_notify(v_chat_msg_id, v_temp);

end;
