RULES_ITEM.SQL_TEXT:
begin
    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.action_items.tt
    ) loop
        pkg_action_item_managment.set_age_of_last_update(rec.xitor_id);
    end loop;
end;

begin
    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.action_items.tt
    ) loop
        pkg_action_item_managment.set_age_of_action_item(rec.xitor_id);
    end loop;
end;

begin
    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.action_items.tt
    ) loop
        pkg_action_item_managment.set_action_due(rec.xitor_id);
    end loop;
end;

begin
    for rec in (
        select xitor_id
          from xitor
         where xitor_type_id = id.action_items.tt
    ) loop
        pkg_action_item_managment.set_days_until_due(rec.xitor_id);
    end loop;
end;

DB_PACKAGES_ITEM.DB_PACKAGE_SPEC:
CREATE OR REPLACE EDITIONABLE PACKAGE "PKG_ACTION_ITEM_MANAGMENT" as

    c_flag constant varchar2(4000) := 'ACT_ACTION_COMMENTS to chat ';    

    procedure action_comment_history(p_xitor_id in xitor.xitor_id%type);
    procedure action_comment_to_chat(p_xitor_id in xitor.xitor_id%type);
    procedure chat_to_action_comment(p_chat_message_id in chat_message.chat_message_id%type);
    procedure set_action_due(p_xitor_id in xitor.xitor_id%type);
    procedure set_age_of_action_item(p_xitor_id in xitor.xitor_id%type);
    procedure set_age_of_last_update(p_xitor_id in xitor.xitor_id%type);
    procedure set_days_until_due(p_xitor_id in xitor.xitor_id%type);
    procedure set_time_last_update(p_xitor_id in xitor.xitor_id%type);
    procedure send_action_item_notification;

end;

DB_PACKAGES_ITEM.DB_PACKAGE_BODY:
CREATE OR REPLACE EDITIONABLE PACKAGE BODY "PKG_ACTION_ITEM_MANAGMENT" as

    procedure action_comment_history(p_xitor_id in xitor.xitor_id%type) as
        v_comment config_value_char.value_clob%type;
    begin
        v_comment := util.getvalmemobyid(p_xitor_id, id.action_items.cf.act_action_comments);
        if v_comment is null then
            return; 
        end if;
        util.setvalmemobyid(p_xitor_id, id.action_items.cf.act_action_comment_history, util.getcurrentuser() || ' ' || to_char(current_date, 'mm/dd/yyyy hh12:miAM') || chr(13) || v_comment || chr(13) || util.getvalstrbyid(p_xitor_id, id.action_items.cf.act_action_comment_history));
        util.setvalmemobyid(p_xitor_id, id.action_items.cf.act_action_comments, null);
    end action_comment_history;

    procedure action_comment_to_chat(p_xitor_id in xitor.xitor_id%type) as
        v_chat_msg_id chat_message.chat_message_id%type;
        v_chat_id chat.chat_id%type;
        v_ret_str varchar2(4000);
    begin

        if util.getvalstrbyid(p_xitor_id, id.action_items.cf.act_action_comments) is null 
        or util.isflagset(c_flag || p_xitor_id) = 1
        then
            util.clearflag(c_flag || p_xitor_id);
            return;
        else
            util.setflag(c_flag || p_xitor_id);
        end if;

        v_chat_id := util.gettrackorchatid(p_xitor_id);
        if v_chat_id is null then
            v_chat_id := pkg_chat.create_trackor_chat(p_xitor_id);
        end if;

        v_chat_msg_id := pkg_chat.create_msg_temp(v_chat_id, pkg_sec.get_cu(), util.getvalstrbyid(p_xitor_id, id.action_items.cf.act_action_comments));
        pkg_chat.finalize_msg_and_notify(v_chat_msg_id, v_ret_str);

    end action_comment_to_chat;

    procedure chat_to_action_comment(p_chat_message_id in chat_message.chat_message_id%type) as
        v_xitor_id xitor.xitor_id%type;
        v_text chat_message.text%type;
    begin

        select c.trackor_id, 
               cm.text
          into v_xitor_id, 
               v_text
          from chat c
          join chat_message cm on cm.chat_id = c.chat_id
         where cm.chat_message_id = p_chat_message_id;

        if util.isflagset(c_flag || v_xitor_id) = 1 then
            util.clearflag(c_flag || v_xitor_id);
            return;
        else
            util.setflag(c_flag || v_xitor_id);
        end if;

        util.setvalcharbyid(v_xitor_id, id.action_items.cf.act_action_comments, v_text);

    end chat_to_action_comment;

    procedure set_action_due(p_xitor_id in xitor.xitor_id%type) as
        v_act_due_date config_value_date.value_date%type;
    begin

        v_act_due_date := util.getvaldatebyid(p_xitor_id, id.action_items.cf.act_due__forecast_date);
        if nvl(util.getvalnumbyid(p_xitor_id, id.action_items.cf.act_action_status), -1) <> id.vt.v_act_action_status.opened then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, null, 1);
            return;
        end if;

        if v_act_due_date is null then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, null, 1);
        elsif v_act_due_date - current_date < 0 then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.past_due, 1);
        elsif v_act_due_date - current_date <= 7 then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.within_seven_days, 1);
        elsif v_act_due_date - current_date <= 30 then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.within_thirty_days, 1);
        elsif v_act_due_date - current_date <= 60 then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.within_sixty_days, 1);
        elsif v_act_due_date - current_date <= 90 then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.within_ninety_days, 1);
        else
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_action_due, id.vt.v_act_acton_due.over_ninety_days, 1);
        end if;

    end set_action_due;

    procedure set_age_of_action_item(p_xitor_id in xitor.xitor_id%type) as
    begin

        for rec in (
            select x.xitor_id
              from xitor x
              join config_value_char cvc on
                  cvc.key_value = x.xitor_id
                  and cvc.config_field_id = id.action_items.cf.act_age_of_action_item
                  and cvc.value_char is not null
              join config_value_number cvn on
                  cvn.key_value = x.xitor_id
                  and cvn.config_field_id = id.action_items.cf.act_action_status
                  and cvn.value_number <> id.vt.v_act_action_status.opened
             where x.xitor_type_id = id.action_items.tt
               and x.xitor_id = p_xitor_id
        ) loop
            util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, null);
        end loop;

        for rec in (
            select x.xitor_id,
                   cvd.value_date
              from xitor x
              join config_value_date cvd on
                  cvd.key_value = x.xitor_id
                  and cvd.config_field_id = id.action_items.cf.act_action_created_date
                  and cvd.value_date is not null
              join config_value_number cvn on
                  cvn.key_value = x.xitor_id
                  and cvn.config_field_id = id.action_items.cf.act_action_status
                  and cvn.value_number = id.vt.v_act_action_status.opened
             where x.xitor_type_id = id.action_items.tt
               and x.xitor_id = p_xitor_id
        ) loop
            if round((current_date - rec.value_date) * 24 * 60) >= 1440 then
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date))) || ' Days');
            elsif round((current_date - rec.value_date) * 24 * 60) >= 60 then
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date) * 24)) || ' Hours');
            else
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_action_item, to_char(round((current_date - rec.value_date) * 24 * 60)) || ' Minutes');
            end if;
        end loop;

    end set_age_of_action_item;

    procedure set_age_of_last_update(p_xitor_id in xitor.xitor_id%type) as
    begin

        for rec in (
            select x.xitor_id
              from xitor x
              join config_value_char cvc on
                  cvc.key_value = x.xitor_id
                  and cvc.config_field_id = id.action_items.cf.act_age_of_last_update
                  and cvc.value_char is not null
              join config_value_number cvn on
                  cvn.key_value = x.xitor_id
                  and cvn.config_field_id = id.action_items.cf.act_action_status
                  and cvn.value_number <> id.vt.v_act_action_status.opened
             where x.xitor_type_id = id.action_items.tt
               and x.xitor_id = p_xitor_id
        ) loop
            util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, null);
        end loop;

        for rec in (
            select x.xitor_id,
                   cvd.value_date
              from xitor x
              join config_value_date cvd on
                  cvd.key_value = x.xitor_id
                  and cvd.config_field_id = id.action_items.cf.act_time_last_update
                  and cvd.value_date is not null
              join config_value_number cvn on
                  cvn.key_value = x.xitor_id
                  and cvn.config_field_id = id.action_items.cf.act_action_status
                  and cvn.value_number = id.vt.v_act_action_status.opened
             where x.xitor_type_id = id.action_items.tt
               and x.xitor_id = p_xitor_id
        ) loop
            if round((current_date - rec.value_date) * 24 * 60) >= 1440 then
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date))) || ' Days');    
            elsif round((current_date - rec.value_date) * 24 * 60) >= 60 then
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date) * 24)) || ' Hours');
            else
                util.setvalcharbyid(rec.xitor_id, id.action_items.cf.act_age_of_last_update, to_char(round((current_date - rec.value_date) * 24 * 60)) || ' Minutes');
            end if;
        end loop;

    end set_age_of_last_update;

    procedure set_days_until_due(p_xitor_id in xitor.xitor_id%type) as
        v_date config_value_date.value_date%type;
    begin
        v_date := util.getvaldatebyid(p_xitor_id, id.action_items.cf.act_due_date);
        if nvl(util.getvalnumbyid(p_xitor_id, id.action_items.cf.act_action_status), -1) <> id.vt.v_act_action_status.opened then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_days_until_due, 0);
        elsif v_date is null then
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_days_until_due, null);
        else
            util.setvalnumbyid(p_xitor_id, id.action_items.cf.act_days_until_due, round(v_date - current_date));
        end if;
    end set_days_until_due;

    procedure set_time_last_update(p_xitor_id in xitor.xitor_id%type) as
        v_date config_value_date.value_date%type;
    begin
        select max(ts)
          into v_date
          from audit_log_cflite
         where column_name = to_char(id.action_items.cf.act_action_comment_history)
           and pk = p_xitor_id;   
        util.setvaldatebyid(p_xitor_id, id.action_items.cf.act_time_last_update, v_date);
    exception when no_data_found then
        util.setvaldatebyid(p_xitor_id, id.action_items.cf.act_time_last_update, null);
    end set_time_last_update;

    procedure send_action_item_notification as
        v_project_id xitor.xitor_id%type;
        v_project_key xitor.xitor_key%type;
        v_email users.email%type;
    begin

        for rec in (
            select x.xitor_id, 
                   x.xitor_key, 
                   u.email
              from xitor x
              join config_value_number cvn1 on 
                  cvn1.key_value = x.xitor_id 
                  and cvn1.config_field_id = id.action_items.cf.act_enable_notifications 
                  and cvn1.value_number = 1
              join config_value_number cvn2 on 
                  cvn2.key_value = x.xitor_id
                  and cvn2.config_field_id = id.action_items.cf.act_action_status
                  and cvn2.value_number = id.vt.v_act_action_status.opened
              join config_value_number cvn3 on 
                  cvn3.key_value = x.xitor_id 
                  and cvn3.config_field_id = id.action_items.cf.act_action_assigned_to 
                  and cvn3.value_number is not null
              join users u on u.xitor_id = cvn3.value_number
              join config_value_date cvd on 
                  cvd.key_value = x.xitor_id 
                  and cvd.config_field_id = id.action_items.cf.act_future_notification_date 
                  and cvd.value_date = current_date
             where x.xitor_type_id = id.action_items.tt
        ) loop

            v_project_id := util.getparentbyid(rec.xitor_id, id.project.tt);
            if v_project_id is not null then
                v_project_key := util.getvalstrbyid(v_project_id, id.project.cf.xitor_key);
            end if;

            select listagg(u.email, ', ' on overflow truncate) within group (order by u.email)
              into v_email
              from config_value_mult cvm
              join users u on u.xitor_id = cvm.value_number
             where cvm.key_value = rec.xitor_id
               and cvm.config_field_id = id.action_items.cf.act_action_followers;
            if v_email is null then
                v_email := rec.email;
            else
                v_email := rec.email || ', ' || v_email;
            end if;

            util.sendmail(
                util.formatlabel(-64632, pkg_label.list_label_params()),
                util.formatlabel(-64633, pkg_label.list_label_params('trackor_link' => pkg_rpt.get_trackor_edit_url(rec.xitor_id), 'xitor_key' => rec.xitor_key, 'v_project_key' => v_project_key, 'act_action_item_name' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_action_item_name), 'act_action_status' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_action_status), 'act_action_category' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_action_category), 'act_priority' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_priority), 'act_department_owner' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_department_owner), 'act_due__forecast_date' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_due__forecast_date), 'act_future_notification_date' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_future_notification_date), 'act_action_assigned_to' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_action_assigned_to), 'act_action_comments' => util.getvalstrbyid(rec.xitor_id, id.action_items.cf.act_action_comments))),
                '',
                v_email,
                null,
                null,
               'html'
            );

        end loop;

    end send_action_item_notification;

end;
